import React from "react";
import "./BSC.css"; // Reusing existing BSC styles

const SecondarySection = () => {
  return (
    <div className="bsc-page">
      <h1 className="page-title">Secondary Section (IX & X)</h1>
      <p className="intro-text">
        The academic session of the year starts in June and finishes in April.
        <br />
        <strong>School timing:</strong> 07.00 am to 11.50 am
      </p>
      <p className="intro-text">
        Secondary education is the obligation of any kind of progress â€” be it for the individual or the nation as a whole. It is only through secondary education that an individual becomes capable of grasping the problems of politics and political processes and thus becomes competent enough for casting their vote in the right favour.
      </p>
    </div>
  );
};

export default SecondarySection;
